package com.java.string;

public class ReplaceAllExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String str = "JavaTpoint";  
		System.out.println(str);    
		String regex = "";  
		str = str.replaceAll(regex, "//");  
		  
		System.out.println(str);  
	}

}
